import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [ users, setUser ] = useState([]);

  useEffect(() => {
        getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get('http://localhost:5000/users');
    setUser(response.data);
  };

  return (
    <div>
      <h1 class="text-xl mb-2">User Lists</h1>
      <div class="overflow-auto rounded-lg shadow hidden md:block">
        <table class="w-full">
          <thead class="bg-gray-50 border-b-2 border-gray-200">
          <tr>
            <th class="w-20 p-3 text-sm font-semibold tracking-wide text-left">No.</th>
            <th class="p-3 text-sm font-semibold tracking-wide text-left">Name</th>
            <th class="w-24 p-3 text-sm font-semibold tracking-wide text-left">Email</th>
            <th class="w-24 p-3 text-sm font-semibold tracking-wide text-left">Gender</th>
            <th class="w-32 p-3 text-sm font-semibold tracking-wide text-left">#</th>
          </tr>
          </thead>
          <tbody class="divide-y divide-gray-100">
            {users.map((user, index) => (
              <tr class="bg-white" key="{index}">
                <td class="p-3 text-sm text-gray-700 whitespace-nowrap">{index + 1}</td>
                <td class="p-3 text-sm text-gray-700 whitespace-nowrap">
                  <a href="#" class="font-bold text-blue-500 hover:underline">{user.name}</a>
                </td>
                <td class="p-3 text-sm text-gray-700 whitespace-nowrap">{user.email}</td>
                <td class="p-3 text-sm text-gray-700 whitespace-nowrap">
                  <span class="p-1.5 text-xs font-medium uppercase tracking-wider text-green-800 bg-green-200 rounded-lg bg-opacity-50">{user.gender}</span>
                </td>
                <td class="p-3 text-sm text-gray-700 whitespace-nowrap"><a href="#" class="font-bold text-red-500 hover:underline">delete</a></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div class="grid grid-cols-1 sm:grid-cols-2 gap-4 md:hidden">
        {users.map((user, index) => 
          <div class="bg-white space-y-3 p-4 rounded-lg shadow" key="{index}">
            <div class="flex items-center space-x-2 text-sm">
              <div class="text-gray-500">{index + 1}</div>
              <div>
                <a href="#" class="text-blue-500 font-bold hover:underline">{user.name}</a>
              </div>
              <div>
                <span
                  class="p-1.5 text-xs font-medium uppercase tracking-wider text-green-800 bg-green-200 rounded-lg bg-opacity-50">{user.gender}</span>
              </div>
            </div>
            <div class="text-sm text-gray-700">
              {user.email}
            </div>
            <div class="font-medium text-black">
            <a href="#" class="p-1.5 text-xs font-bold bg-red-500 text-white tracking-wider rounded-xl bg-opacity-50">delete</a>
            </div>
          </div>
        )}
      </div>
    </div>
  );

};

export default UserList;
