import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [ users, setUser ] = useState([]);

  useEffect(() => {
        getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get('https://localhost:5000/users');
    setUser(response.data);
  };

  return (
    <div class='rounded-lg border-2 border-zinc-500 w-4/5 p-2'>
      <table class='table-auto w-full'>
        <thead class='border-b-2 border-slate-500'>
          <tr>
            <th>No.</th>
            <th>Name</th>
            <th>Gender</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
                {users.map((user, index) => (
                        <tr class='border-b-2 border-slate-300' key="{user.id}">
                        <td>{index + 1}</td>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.gender}</td>
                        <td>
                        <button
                                class='text-blue-500 background-transparent font-bold uppercase px-3 py-1 text-xs outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150'
                                type='button'
                        >
                                edit
                        </button>
                        |
                        <button
                                class='text-red-500 background-transparent font-bold uppercase px-3 py-1 text-xs outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150'
                                type='button'
                        >
                                delete
                        </button>
                        </td>
                        </tr>
                ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
