import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export const Login = () => {
    const [email, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const login = async (e) => {
        e.preventDefault();
        try {
            await axios.post(`${process.env.REACT_APP_API_ROOT}/api/login`, {
                email,
                password,
            });
            navigate('/users');
        } catch (error) {
            console.error(error);
        }
    };
    return (
        <div className='min-h-screen flex flex-col items-center justify-center'>
            <div className='bg-white drop-shadow-md rounded-md flex flex-col items-center w-96 p-5'>
                <h1 class='text-xl font-bold leading-tight tracking-tight text-gray-900 md:text-2xl mb-4'>
                    Sign in to your account
                </h1>
                <form className='w-full max-w-xs'>
                    <div className='mb-4'>
                        <label
                            htmlFor='email'
                            className='block text-gray-700 text=sm font-bold mb-2'
                        >
                            Email
                        </label>
                        <input
                            type='text'
                            placeholder='Email'
                            className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading tight focus:outline-blue-300'
                        />
                    </div>

                    <div className='mb-4'>
                        <label
                            htmlFor='password'
                            className='block text-gray-700 text=sm font-bold mb-2'
                        >
                            Password
                        </label>
                        <input
                            type='password'
                            placeholder='********'
                            className='shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading tight focus:outline-blue-300'
                        />
                    </div>

                    <div class='flex items-center justify-between'>
                        <button
                            class='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full'
                            type='submit'
                        >
                            Sign In
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};
